options.customCss:
:root {
  /* Fonts */
  --font-family: 'Open Sans', sans-serif;
  --font-weight-normal: 400;
  --font-weight-bold: 600;

  /* Font Sizes */
  --font-size-body: 14px;
  --font-size-label: 14px;
  --font-size-input: 15px;
  --font-size-header: 24px;
  --font-size-paragraph: 15px;
  --font-size-link: 14px;
  --font-size-error: 13px;
  --font-size-subheader: 16px;
  --font-size-html-h1: 30px;
  --font-size-html-h2: 24px;
  --font-size-html-h3: 20px;
  --font-size-html-h4: 18px;
  --font-size-html-h5: 15px;
  --font-size-html-h6: 13px;
  --font-size-test-notice: 13px;

  /* Colors - Theme */
  --color-background: #f9fafb;
  --color-card-bg: #ffffff;
  --color-card-border: #e0e4ec;
  --color-card-shadow: rgba(0, 0, 0, 0.04);

  --color-header: #333333;
  --color-header-subtext: #7e8186;
  --color-label: #555555;
  --color-html-text: #555555;
  --color-html-link: #ff6d5a;

  /* Colors - Input & UI */
  --color-input-border: #cfd4dc;
  --color-input-text: #4a4f57;
  --color-focus-border: #5a4cca;

  /* Colors - Buttons */
  --color-submit-btn-bg: #ff6d5a;
  --color-submit-btn-text: #ffffff;
  --color-clear-button-bg: #7e8186;

  /* Colors - Notices & Alerts */
  --color-test-notice-text: #e6a23d;
  --color-test-notice-bg: #fefaf6;
  --color-test-notice-border: #f6dcb7;
  --color-error: #ea1f30;
  --color-required: #ff6d5a;

  /* Border Radius */
  --border-radius-card: 10px;
  --border-radius-input: 8px;
  --border-radius-clear-btn: 50%;

  /* Spacing */
  --padding-container-top: 32px;
  --padding-card: 24px;
  --padding-form-input: 14px;
  --margin-bottom-card: 24px;
  --padding-test-notice-vertical: 14px;
  --padding-test-notice-horizontal: 24px;

  /* Dimensions */
  --container-width: 500px;
  --submit-btn-height: 48px;
  --checkbox-size: 18px;

  /* Shadows */
  --box-shadow-card: 0 4px 12px rgba(0, 0, 0, 0.06);

  /* Misc */
  --opacity-placeholder: 0.5;
}

parameters.message:
=<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <title>Email</title>
  <meta name="viewport" content="width=device-width, initial-scale=1.0">

  <!-- Google Font -->
  <link href="https://fonts.googleapis.com/css2?family=Open+Sans:wght@400;600&display=swap" rel="stylesheet">

  <style>
    body, table, td, a {
      -webkit-text-size-adjust: 100%;
      -ms-text-size-adjust: 100%;
    }

    body {
      margin: 0;
      padding: 0;
      background-color: #f4f4f4;
      font-family: 'Open Sans', Arial, sans-serif;
    }

    table {
      border-collapse: collapse !important;
    }

    a {
      color: inherit;
      text-decoration: none;
    }

    .cta-button {
      display: inline-block;
      background-color: #2d89ef;
      color: #ffffff !important;
      padding: 14px 28px;
      border-radius: 8px;
      font-weight: bold;
      font-size: 16px;
      box-shadow: 0 4px 14px rgba(45, 137, 239, 0.3);
      transition: all 0.35s ease;
      transform: scale(1);
    }

    .cta-button:hover {
      background-color: #1a6ed8;
      box-shadow: 0 6px 20px rgba(45, 137, 239, 0.5);
      transform: scale(1.03);
    }

    @media (prefers-reduced-motion: no-preference) {
      .cta-button {
        transition: background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease;
      }
    }

    @media only screen and (max-width: 600px) {
      .container {
        width: 100% !important;
        padding: 20px !important;
      }

      .cta-button {
        width: 100% !important;
        text-align: center !important;
      }
    }
  </style>
</head>

<body>
  <table width="100%" cellpadding="0" cellspacing="0">
    <tr>
      <td align="center" style="padding: 40px 10px;">
        <table class="container" width="600" style="max-width: 600px; background-color: #ffffff; border-radius: 12px; overflow: hidden; box-shadow: 0 8px 24px rgba(0, 0, 0, 0.06);">

          <!-- Header -->
          <tr>
            <td align="center" style="background-color: #2d89ef; color: #ffffff; padding: 40px 30px; font-size: 28px; font-weight: 600; letter-spacing: 0.5px;">
              {{ $('email_variables').item.json.email_header }}
            </td>
          </tr>

          <!-- Body -->
          <tr>
            <td style="padding: 32px 30px; color: #333333; font-size: 17px; line-height: 1.6;">
              <p style="margin-top: 0;">Dear <strong>{{ $('client_info').item.json['First Name'] }}</strong>,</p>

              <p><strong>{{ $('email_variables').item.json.welcome_message }}</strong></p>

              <p>{{ $('email_variables').item.json.invitation_message }}</p>

              <p><strong>{{ $('email_variables').item.json.cta_intro }}</strong></p>

              <p style="text-align: center; margin: 40px 0;">
                <a href="{{ $('email_variables').item.json.cta_link }}" class="cta-button">
                  {{ $('email_variables').item.json.cta_text }} &rarr;
                </a>
              </p>

              <p>{{ $('email_variables').item.json.closing_message }}</p>

              <p style="margin-bottom: 0;">
                Best regards,<br><br>
                <strong>{{ $('email_variables').item.json.sender_first_name }} {{ $('email_variables').item.json.sender_last_name }}</strong><br>
                {{ $('email_variables').item.json.sender_position }}<br>
                {{ $('email_variables').item.json.sender_company }}<br>
                <a href="mailto:{{ $('email_variables').item.json.sender_contact }}" style="color: #2d89ef; text-decoration: none;">
                  {{ $('email_variables').item.json.sender_contact }}
                </a>
              </p>
            </td>
          </tr>

          <!-- Divider -->
          <tr>
            <td>
              <hr style="border: none; border-top: 1px solid #e0e0e0; margin: 0 30px;">
            </td>
          </tr>

          <!-- Footer -->
          <tr>
            <td align="center" style="background-color: #f9f9f9; color: #999999; font-size: 12px; padding: 20px 30px;">
              {{ $('email_variables').item.json.footer_text }}
            </td>
          </tr>
        </table>
      </td>
    </tr>
  </table>
</body>
</html>
